import React,{ Component } from 'react';
import {Redirect} from 'react-router-dom';
import axios from 'axios';
import propTypes from 'prop-types';

class newuser extends Component {
	constructor(props){
		super(props);
		this.state = {
			name:'',
			nameValid:false,
			usersData:[]		
		}
		this.handle = this.handle.bind(this);
	}	

	handle(event){
		var regex = /[A-Za-z ]+/;
		if(regex.test(event.target.value) == false){
			console.log('Not Valid');
		}
		this.setState({name:event.target.value});
	}

	componentDidMount(){
		axios.get('https://test-9515d.firebaseio.com/users.json')
			.then((res) => {
				const fetchOrders = [];
				for(let key in res.data){
					fetchOrders.push({
						...res.data[key],
						id:key
					});
				}
				this.setState({usersData:fetchOrders});
			});
	}

	push(){
		axios.post('https://test-9515d.firebaseio.com/users.json',  {
    "id": 5,
    "name": "Chelsey Dietrich",
    "username": "Kamren",
    "email": "Lucio_Hettinger@annie.ca",
    "address": {
      "street": "Skiles Walks",
      "suite": "Suite 351",
      "city": "Roscoeview",
      "zipcode": "33263",
      "geo": {
        "lat": "-31.8129",
        "lng": "62.5342"
      }
    },
    "phone": "(254)954-1289",
    "website": "demarco.info",
    "company": {
      "name": "Keebler LLC",
      "catchPhrase": "User-centric fault-tolerant solution",
      "bs": "revolutionize end-to-end systems"
    }
  })
	}

	render(){
		return(
			<div>
				{false? <Redirect to="/login" />:null}
				<p>New User</p>
				<input type="text" value={this.state.name} onChange={this.handle}/>
				<b>{this.state.name}</b>
				{this.state.usersData.map((x) => <div><p>{x.name}</p></div>)}
				<button onClick={this.push}>Push</button>
			</div>
		);
	}
}

newuser.propTypes = {
	name:propTypes.string
}

export default newuser;
