import React, { Component } from 'react';
import axios from 'axios';

import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state={
      Likes:[]
    }
    this.getDataFunction = this.getDataFunction.bind(this);
  }

  getDataFunction = () => { 
    axios.get('http://test.peppersquare.com/api/v1/article')
      .then((res) => this.setState({Likes:res.data}))
  }

  render() {
    console.log(this.state.Likes);
    return (
      <div>
        <p>This is pepper square</p>
        <button onClick={this.getDataFunction}>get data</button>
        <div className="displayparent">
          {this.state.Likes.map((res) => <div className="displayChild">{res.title}</div>)}
        </div>
      </div>
    );
  }
}

export default App;
