import React, { Component } from 'react';
import axios from 'axios';

import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state={
      Likes:[]
    }
  }

  componentDidMount(){
    axios.get('http://test.peppersquare.com/api/v1/article')
      .then((res) => this.setState({Likes:res.data.filter((resp) => resp.image !== '')}))   
  }

  render() { 
    console.log(this.state.Likes);
    console.log(this.state.Likes.filter((res) => res.image !== ''));
    return (
      <div>
        <div className="displayparent">
          {this.state.Likes.filter((res) => res.image !== '').map((res,i) => <div className="displayChild" key={i}>
            <div className="displayImage">
              <img src={res.image} alt="No Image Present" />
            </div>
            <div className="displayDescription">
              <b>{res.title}</b>
              <p>{res.description}</p>
            </div>
          </div>)}
        </div>
      </div>
    );
  }
}

export default App;
