import React from 'react';

import './detail.css';

const detail = (props) => {
	return(
		<div className="displayChild" onClick={props.displayChildClicked} >
			<div className="displayChildImage">
				<img src={props.img} />
			</div>
			<div className="displayChildDetails">
				<b>{props.title}</b>
				<p>{props.description}</p>
			</div>			
		</div>
	)
}

export default detail;
