import React,{ Component } from 'react';
import axios from '../../axios-users';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';

class user extends Component {
	constructor(props){
		super(props);
		this.state = {
			facebook:[],
			google:[]
		}
	}	

	responseFacebook = response => {
		console.log(response)
		this.setState({facebook:response})
	}

	componentClicked = () => {
		console.log('clicked');
	}

	responseGoogle = response => {
		console.log(response);
		this.setState({google:response})
	}

	render(){	

		let fbContent;

		if(this.state.isLoggedIn){
			fbContent = null;
		}
		else{
			fbContent=(
				<FacebookLogin
				    appId="394732927768812"
				    autoLoad={true}
				    fields="name,email,picture"
				    onClick={this.componentClicked}
				    callback={this.responseFacebook} />	
			);	    		
		}
		console.log(this.state.facebook);
		console.log(this.state.google.tokenObj.token_type);
		return(
			<div>
				{fbContent}
				<p>{this.state.google.googleId}</p>
				<GoogleLogin
					clientId="341529182538-lut1n6vudrk7tc3hcglaff8q9n8na08j.apps.googleusercontent.com"
					buttonText="Login"
					onSuccess={this.responseGoogle}
					onFailure={this.responseGoogle}
				/>				
			</div>
		);
	}
}

export default user;
