import React, { Component } from 'react';
import axios from 'axios';

import './App.css';
import Detail from './components/detail/detail';

class App extends Component {
  constructor(){
    super();
    this.state={
      Likes:[]
    }
  }

  componentDidMount(){
    axios.get('http://test.peppersquare.com/api/v1/article')
      .then((res) => this.setState({Likes:res.data.filter((resp) => resp.image !== '')}))   
  }

  addDefaultSrc(ev){
    ev.target.src = 'http://media.istockphoto.com/photos/plant-growing-picture-id510222832?k=6&m=510222832&s=612x612&w=0&h=Pzjkj2hf9IZiLAiXcgVE1FbCNFVmKzhdcT98dcHSdSk='
  }

  render() { 
    return (
      <div>
      <Detail />
        <div className="displayparent">
          {this.state.Likes.map((res,i) => <div className="displayChild" key={i}>
            <div className="displayImage">
              <img src={res.image} onError={this.addDefaultSrc} alt="No Image Present" />
            </div>
            <div className="displayDescription">
              <b>{res.title}</b>
              <p>{res.description}</p>
            </div>
          </div>)}
        </div>
      </div>
    );
  }
}

export default App;
