import React, { Component } from 'react';
import axios from 'axios';
import {Route,Link} from 'react-router-dom';

import './App.css';
import Detail from './components/detail/detail';
import Create from './components/create/create';
import Home from './components/home/home';

class App extends Component {
  constructor(){
    super();
    this.state={
      Likes:[]
    }
    this.getDetails = this.getDetails.bind(this);
  }

  componentDidMount(){
    axios.get('http://test.peppersquare.com/api/v1/article')
      .then((res) => this.setState({Likes:res.data}))   
  }

  getDetails = (a) => {
    console.log('get details',a);
  }

  render() { 
    return (
      <div>
          <Link to ="/">Home</Link>
          <Link to ="/create">Create</Link>
          <Route path="/" exact render={(props) => <Home {...props} />} component={Home} />
          <Route path="/create" component={Create} />
          <Route path="/detail" component={Detail} />
        <div className="displayparent">
          {/*this.state.Likes.map((res,i) => {
            return(
              <Detail 
              key={i}
              id={res.id} 
              img={res.image} 
              description={res.description}
              published={res.published}
              author={res.author}
              title={res.title}
              tags={res.tags}
              displayChildClicked={() => this.getDetails(res.id)} />
            )
          })*/}
        </div>
      </div>
    );
  }
}

export default App;
