import React, { Component } from 'react';
import axios from 'axios';

import './App.css';
import Detail from './components/detail/detail';

class App extends Component {
  constructor(){
    super();
    this.state={
      Likes:[]
    }
  }

  componentDidMount(){
    axios.get('http://test.peppersquare.com/api/v1/article')
      .then((res) => this.setState({Likes:res.data}))   
  }

  render() { 
    return (
      <div>
      <Detail />
        <div className="displayparent">
          {this.state.Likes.map((res,i) => {
            return(
              <Detail id={res.id} key={i} />
            )
          })}
        </div>
      </div>
    );
  }
}

export default App;
